        const input = component.refs.addTodoInput;
        input.value = 'This is a new item';
        Simulate.change(input);
        Simulate.keyPress(input, {key: "Enter", keyCode: 13, which: 13});

        expect(addedItem).to.equal('This is a new item');
    });

});

